import React, { useState } from "react";
import "./App.css";
import CourseInput from "./Components/CourseInput";
import Expenses from "./Components/Expenses";

const DUMMY__EXPENSES = [
  {
    text: "Do All Exercises!",
    id: "g1",
  },
  {
    text: "Finish The Course!",
    id: "g2",
  },
];

const App = () => {
  const [expenses, setExpenses] = useState(DUMMY__EXPENSES);
  const saveDataExpense = (expenseData) => {
    setExpenses((prevExpense) => {
      return [expenseData, ...prevExpense];
    });
  };

  const deleteItems = (goalId) => {
    setExpenses(prevId => {
      const updatedGoals = prevId.filter(goal => goal.id !== goalId);
      return updatedGoals;
    })


  }

  return (
    <div>
      <section id="goal-form">
        <CourseInput onAddExpense={saveDataExpense} />
      </section>
      <Expenses items={expenses} onDeleteItems = {deleteItems}/>
    </div>
  );
};

export default App;
