import React, { useState } from "react";
import "./App.css";
import CourseInput from "./Components/CourseInput";
import Expenses from "./Components/Expenses";

const DUMMY__EXPENSES = [
  {
    text: "Do All Exercises!",
    id: "g1",
  },
  {
    text: "Finish The Course!",
    id: "g2",
  },
];

const App = () => {
  const [expenses, setExpenses] = useState(DUMMY__EXPENSES);
  const saveDataExpense = (expenseData) => {
    setExpenses((prevExpense) => {
      return [expenseData, ...prevExpense];
    });
  };

  const saveDeleteExpenses = (enteredId) => {
    setExpenses((prevId) => {
      const updatedGoal = prevId.filter((goalId) => goalId.id !== enteredId);
      return updatedGoal;
    });
  };

  let warning = <p style={{textAlign:'center', color: '#dc6d6d'}}>No goals found. Maybe add one?</p>

  if (expenses.length > 0) {
    warning =  <Expenses items={expenses} onDeleteExpenses={saveDeleteExpenses} />;
  }

  return (
    <div>
      <section id="goal-form">
        <CourseInput onAddExpense={saveDataExpense} />
      </section>
      {warning}
    </div>
  );
};

export default App;
