import React, { useState } from "react";
import "./App.css";
import CourseInput from "./Components/CourseInput";
import Expenses from "./Components/Expenses";

const DUMMY__EXPENSES = [
  {
    text: "Do All Exercises!",
    id: "G1",
  },
  {
    text: "Finish The Course!",
    id: "G2",
  },
];

const App = () => {
  const [expense, setExpense] = useState(DUMMY__EXPENSES);

  const saveExpenseData = (enteredExpenseData) => {
    setExpense((prevExpese) => {
      return [enteredExpenseData, ...prevExpese];
    });
  };

  const deleteHandler = (goalId) => {
    setExpense((prevId) => {
      const updatedId = prevId.filter((goal) => goal.id !== goalId);
      return updatedId;
    });
  };

  return (
    <div>
      <section id="goal-form">
        <CourseInput onAddExpense={saveExpenseData} />
      </section>
      <Expenses items={expense} onDeleteItems={deleteHandler} />
    </div>
  );
};

export default App;
